-- database unit test for dbo.MoveTopic
DECLARE	@RC		AS INT, 
	@TopicID		AS INT, 
	@ParentID		AS INT, 
	@SiblingID		AS INT;

SELECT	@RC		= 0,
	@TopicID		= 0,
	@ParentID		= 0,
	@SiblingID		= 0;

EXECUTE	@RC		= [dbo].[MoveTopic] 
	@TopicID, 
	@ParentID, 
	@SiblingID;

SELECT	@RC		AS RC;

-- database unit test for dbo.UpdateAttributes
DECLARE	@RC		AS INT, 
	@TopicID		AS INT, 
	@Attributes		AS [dbo].[AttributeValues], 
	@Version		AS DATETIME, 
	@DeleteUnmatched	AS BIT;

SELECT	@RC		= 0,
	@TopicID		= 0,
	@Version		= getdate(),
	@DeleteUnmatched	= 0;

EXECUTE	@RC		= [dbo].[UpdateAttributes]
	@TopicID,
	@Attributes,
	@Version,
	@DeleteUnmatched;

SELECT	@RC		AS RC;

