-- database unit test for dbo.UpdateTopic
DECLARE	@RC		AS INT,
	@TopicID		AS INT,
	@Key		AS VARCHAR (128),
	@ContentType		AS VARCHAR (128),
	@Attributes		AS [dbo].[AttributeValues],
	@ExtendedAttributes	AS XML,
	@Version		AS DATETIME,
	@DeleteUnmatched	AS BIT;

SELECT	@RC		= 0,
	@TopicID		= 0,
	@Key		= NULL,
	@ContentType		= NULL,
	@ExtendedAttributes	= NULL,
	@Version		= GETUTCDATE(),
	@DeleteUnmatched	= 0;

EXECUTE	@RC		= [dbo].[UpdateTopic]
	@TopicID,
	@Key,
	@ContentType,
	@Attributes,
	@ExtendedAttributes,
	@Version,
	@DeleteUnmatched;

SELECT	@RC		AS RC;

--------------------------------------------------------------------------------------------------------------------------------
-- DECLARE VARIABLES
--------------------------------------------------------------------------------------------------------------------------------
DECLARE	@TopicID		AS INT,
	@Key		AS VARCHAR (128),
	@ContentType		AS VARCHAR (128),
	@ParentID		AS INT,
	@Attributes		AS [dbo].[AttributeValues],
	@ExtendedAttributes	AS XML,
	@References		AS [dbo].[TopicReferences],
	@Version		AS DATETIME;

--------------------------------------------------------------------------------------------------------------------------------
-- DELETE TEST DATA
--------------------------------------------------------------------------------------------------------------------------------
DELETE	
FROM	Attributes
WHERE	AttributeKey		LIKE 'DeleteTopicTest%'

DELETE
FROM	Relationships
WHERE	RelationshipKey		= 'DeleteTopicTest'

DELETE
FROM	TopicReferences
WHERE	ReferenceKey		= 'DeleteTopicTest'

DELETE	
FROM	Topics
WHERE	TopicKey		LIKE 'DeleteTopic%'

--------------------------------------------------------------------------------------------------------------------------------
-- SET VARIABLES
--------------------------------------------------------------------------------------------------------------------------------
SELECT	@Key		= 'DeleteTopicTest',
	@ContentType		= 'Test',
	@ParentID		= NULL,
	@ExtendedAttributes	= '<Attributes><Attribute key=''Body''>Test</Attribute></Attributes>',
	@Version		= GETUTCDATE();

INSERT
INTO	@Attributes
VALUES	( 'DeleteTopicTest1', 'Value' ),
	( 'DeleteTopicTest2', 'Value' )

--------------------------------------------------------------------------------------------------------------------------------
-- ESTABLISH TEST DATA
--------------------------------------------------------------------------------------------------------------------------------
EXECUTE	@TopicID		= [dbo].[CreateTopic]
	@Key,
	@ContentType,
	@ParentID,
	@Attributes,
	@ExtendedAttributes,
	@References,
	@Version;

SELECT	@Key		= 'DeleteTopicChildTest',
	@ParentID		= @TopicID;

EXECUTE	@TopicID		= [dbo].[CreateTopic]
	@Key,
	@ContentType,
	@ParentID,
	@Attributes,
	@ExtendedAttributes,
	@References,
	@Version;

INSERT
INTO	Relationships (
	  Source_TopicID,
	  RelationshipKey,
	  Target_TopicID
	)
VALUES	( @TopicID,
	  'DeleteTopicTest',
	  @ParentID
	)

INSERT
INTO	TopicReferences (
	  Source_TopicID,
	  ReferenceKey,
	  Target_TopicID
	)
VALUES	( @TopicID,
	  'DeleteTopicTest',
	  @ParentID
	)

--------------------------------------------------------------------------------------------------------------------------------
-- VERIFY RESULTS
--------------------------------------------------------------------------------------------------------------------------------
SELECT	*
FROM	Topics
WHERE	TopicKey		LIKE 'DeleteTopic%'

SELECT	*
FROM	Attributes
WHERE	AttributeKey		LIKE 'DeleteTopic%'

SELECT	*
FROM	Relationships
WHERE	RelationshipKey		LIKE 'DeleteTopic%'

SELECT	*
FROM	TopicReferences
WHERE	ReferenceKey		LIKE 'DeleteTopic%'

