-- UPDATE VARIABLES
--------------------------------------------------------------------------------------------------------------------------------
SELECT	@ExtendedAttributes	= '<Attributes><Attribute key=''Body''>New Test</Attribute></Attributes>';

UPDATE	@Attributes
SET	AttributeValue		= 'NewValue'

--------------------------------------------------------------------------------------------------------------------------------
-- ESTABLISH NEW VERSION
--------------------------------------------------------------------------------------------------------------------------------
EXECUTE	@TopicID		= [dbo].[UpdateTopic]
	@TopicID		= @TopicID,
	@Attributes		= @Attributes,
	@ExtendedAttributes	= @ExtendedAttributes,
	@Version		= @NewVersion;

INSERT
INTO	Relationships (
	  Source_TopicID,
	  RelationshipKey,
	  Target_TopicID,
	  IsDeleted,
	  Version
	)
VALUES	( @ParentID,
	  'GetTopicVersionTest',
	  @TopicID,
	  1,
	  @NewVersion
	)

INSERT
INTO	TopicReferences (
	  Source_TopicID,
	  ReferenceKey,
	  Target_TopicID,
	  Version
	)
VALUES	( @ParentID,
	  'GetTopicVersionTest',
	  NULL,
	  @NewVersion
	)

